(async () => {
  let Prompt = (msg.payload || '').trim()
  if (Prompt === '') {
    msg.payload = 'empty or missing prompt'
    node.send([null, msg])
    node.done()
    return
  }

  /**** retrieve settings or provide defaults ****/

  let Seed = parseInt(msg.seed, 10)
  if (isNaN(Seed)) { Seed = -1 }

  let Threads = parseInt(msg.threads, 10)
  if (isNaN(Threads)) { Threads = 4 }
  Threads = Math.max(1, Threads)
  Threads = Math.min(Threads, Math.max(1, os.cpus().length))

  let ContextLength = parseInt(msg.context, 10)
  if (isNaN(ContextLength)) { ContextLength = 512 }
  ContextLength = Math.max(0, Math.min(ContextLength, 4096))

  let keep = parseInt(msg.keep, 10)
  if (isNaN(keep)) { keep = 0 }
  keep = Math.max(-1, Math.min(keep, ContextLength))

  let PredictionLength = parseInt(msg.predict, 10)
  if (isNaN(PredictionLength)) { PredictionLength = 128 }
  PredictionLength = Math.max(1, Math.min(PredictionLength, ContextLength)) // no -1!

  let topK = parseInt(msg.topk, 10)
  if (isNaN(topK)) { topK = 40 }
  topK = Math.max(1, Math.min(topK, 100))

  let topP = parseFloat(msg.topp)
  if (isNaN(topP)) { topP = 0.9 }
  topP = Math.max(0.1, Math.min(topP, 1.0))

  let Temperature = parseFloat(msg.temperature)
  if (isNaN(Temperature)) { Temperature = 0.8 }
  Temperature = Math.max(0.0, Math.min(Temperature, 1.0))

  let Batches = parseInt(msg.batches, 10)
  if (isNaN(Batches)) { Batches = 8 }
  Batches = Math.max(1, Math.min(Batches, 100))

  let Grammar = (msg.grammar || '').trim()

  Prompt = Prompt.replace(/"/g, '\\"')

  /**** retrieve UserDir - crash if not a folder ****/

  let UserDir = (global.get('UserDir') || '').trim()
  if (UserDir === '') { UserDir = process.env.HOME + '/.node-red' }

  if (!fs.statSync(UserDir, { throwIfNoEntry: false })?.isDirectory()) {
    throw new Error(
      'the given "UserDir" ("' + UserDir + '") is either missing or ' +
      'not a folder - exiting'
    )
  }

  /**** combine all these settings into a command ****/

  const Command = (
    './llama --model ./stablelm-3b-4e1t-Q8_0.bin --mlock ' +
    ' --ctx_size ' + ContextLength + ' --keep ' + keep +
    ' --n_predict ' + PredictionLength +
    ' --threads ' + Threads + ' --batch_size ' + Batches +
    ' --seed ' + Seed + ' --temp ' + Temperature +
    ' --top_k ' + topK + ' --top_p ' + topP +
    ' --reverse-prompt "<|endoftext|>"' +
    (Grammar === '' ? '' : '--grammar "' + Grammar + '"') +
    ' --prompt "' + Prompt + '"'
  )
  const Options = {
    cwd: UserDir
  }

  /**** extract actual reponse from command output ****/

  function ResponseFrom(Text) {
    let HeaderLength = Text.indexOf('\n\n\n')
    Text = Text.slice(HeaderLength + 1)

    let TrailerIndex = Text.indexOf('<|endoftext|>')
    if (TrailerIndex < 0) {
      TrailerIndex = Text.indexOf('\nllama_print_timings')
    }
    Text = Text.slice(0, TrailerIndex)

    return Text
  }

  /**** now infer a response from the given prompt ****/

  node.status({ fill: 'yellow', shape: 'ring', text: 'running' })

  const ShellProcess = child_process.exec(Command, Options)
  let stdout = ''
  for await (const Chunk of ShellProcess.stdout) {
    stdout += Chunk
  }

  let stderr = ''
  for await (const Chunk of ShellProcess.stderr) {
    stderr += Chunk
  }

  const ExitCode = (await new Promise((resolve) => {
    ShellProcess.on('close', resolve)
  })) || 0
  if (ExitCode !== 0) {
    node.status({ fill: 'red', shape: 'dot', text: 'ExitCode = ' + ExitCode })

    msg.statusCode = 500 + ExitCode
    msg.payload = stderr
  } else {
    node.status({ fill: 'green', shape: 'dot', text: 'finished' })

    stdout = stdout.trim()

    msg.statusCode = (stdout === '' ? 204 : 200)
    msg.payload = ResponseFrom(stdout)
  }

  node.send([msg, null])
  node.done()
})()
