  function ResponseFrom(Text) {
    let HeaderLength = Text.indexOf('system_info')
    Text = Text.slice(HeaderLength + 1)
      .replace(/^[^\n]*\n/, '')

    let TrailerIndex = Text.indexOf('\n\nllama_print_timings')
    Text = Text.slice(0, TrailerIndex)

    return Text
  }

  /**** now tokenize the given prompt ****/

  let { stdout, stderr, StatusCode, Signal } = child_process.spawnSync(
    'bash', [], { input: Command }
  )

  const $stdout = stdout.toString().trim()
  const $stderr = stderr.toString().trim()

  switch (true) {
    case (StatusCode == null):
    case (StatusCode === 0):
      msg.statusCode = ($stdout === '' ? 204 : 200)
      msg.payload = ResponseFrom($stdout)
      break
    default:
      msg.statusCode = 500 + StatusCode
      msg.payload = ($stdout === '' ? '' : '>>>> stdout >>>>\n' + $stdout + '\n') +
        '>>>> stderr >>>>\n' + $stderr +
        (Signal == null ? '' : '\n' + Signal)
      break
  }

  node.send([msg, null])
  node.done()
})()

